import React,{Component} from 'react';
import './Modal.css';

class Modal extends Component{
    closeModal = ()=>{
        this.props.closeModal();
      }

   render(){
       if(!this.props.show){
           return null;
       }
    return(
        <div className="Modal">
            <div className='container'>
            <iframe style="margin:auto" width="800%" height="80%" src="https://www.youtube.com/embed/iNVgZGuaiN0" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>
             <div><span id='close' onClick={()=>{this.closeModal();}}>&#10005;</span></div>

            </div>
        </div>
    );
   }

}



export default Modal;
